head.script:
function hideRecorderWindow() {
var rc = getRecorderEl();
if (rc != null) {
rc.parentNode.removeChild(rc);
}
}
function flashRecorderGetRecordingId() {
var recorderWindow = getRecorder();
if (recorderWindow.ymFlashRecorderGetRecordingId != null) {
//alert("id: " + recorderWindow.ymFlashRecorderGetRecordingId());
return recorderWindow.ymFlashRecorderGetRecordingId();
} else {
alert("Unable to get recording id");
}
return null;
}
function getRecorderEl()
{
var vmRecorderDiv = getChildElementById("vmId");
return getChildElementById("flashrecorder1", vmRecorderDiv);
}
function getRecorder()
{
var frameEl = getRecorderEl();
return frameEl.contentWindow;
}

