function setQuotaLvl(perc)
{
var total = 110;
var seen = total * perc / 100;
var hiderLength = total - seen;
var hider = document.getElementById("quotaHiderId");
hider.style.marginLeft = seen + "px";
hider.style.width = hiderLength + "px";
var quotaLabel = document.getElementById("quotaLvlId");
quotaLabel.innerHTML = perc + "";
}

Youmail.vm.initFolderMenu = function() {
var folderActionItems = [
[
{ text: "New Folder", onclick: { fn: folderActionExecute, obj: "New Folder" } },
{ text: "Edit", onclick: { fn: folderActionExecute, obj: "Edit" } },
{ text: "Delete", onclick: { fn: folderActionExecute, obj: "Delete" } }
],
[
{ text: "Mailbox Usage", onclick: { fn: folderActionExecute, obj: "Mailbox Usage" } }
]
];
var folderActionMenu = new YAHOO.widget.ContextMenu(
"folderAction",
{
trigger: new Array(
"folderSysInbox",
"folderSysFlagged",
"folderSysSave",
"folderSysTrash",
"folderSysSpam",
"folderSysHistory"
),
itemdata: folderActionItems,
lazyload: true
}
);
// Add a "render" event handler to the field context menu
folderActionMenu.subscribe("show", onFolderActionMenuRender);
function folderActionExecute(p_sType, p_aArgs, p_sAction)
{
var menu = this.parent;
var folderId = findFolderId(menu.contextEventTarget);
if (p_sAction == null) {
// do nothing
} else if (p_sAction == "Delete") {
inboxDeleteFolder(folderId);
} else if (p_sAction == "New Folder") {
openEditFolderDialog(true);
} else if (p_sAction == "Edit") {
openEditFolderDialog(false, folderId);
} else if (p_sAction == "Folder Usage") {
openUrlPanel('Folder Usage Information', '400px', '600px', '/youmail/vm/inbox/getFolderUsageInfo.do?f=' + folderId);
} else if (p_sAction == "Mailbox Usage") {
openUrlPanel('Usage Information', '400px', '600px', '/youmail/vm/inbox/getServiceClassInfo.do');
} else {
// do nothing
}
}
function onFolderActionMenuRender(e)
{
var menu = this;
var folderId = findFolderId(menu.contextEventTarget);
if (folderId == null) {
return;
}
if (folderId.indexOf('folderSys') >= 0) {
// disable delete and edit if system folder
menu.getItem(1).cfg.setProperty("disabled", true);
menu.getItem(2).cfg.setProperty("disabled", true);
} else {
menu.getItem(1).cfg.setProperty("disabled", false);
menu.getItem(2).cfg.setProperty("disabled", false);
}
}
/* find an id that start with 'folder', this will have the folder info, return it. */
function findFolderId(contextEl)
{
if (contextEl == null) {
return null;
}
if (contextEl.id != null) {
if (contextEl.id.indexOf('folder') >= 0) {
// found class name that starts with folderSys folderCus
return contextEl.id;
}
}
return findFolderId(contextEl.parentNode);
}
};

function newTableButtonSubmit(e)
{
var theForm = document.forms["actionForm"];
processAction(theForm.buttonName.value);
YAHOO.util.Event.preventDefault(e); // ie workaround, double submit problem with yui button
}
function tableButtonSubmit(theForm)
{
processAction(theForm.buttonName.value);
return false;
}
function processAction(buttonName, optEntryId)
{
var entryIds;
if (optEntryId == null) {
entryIds = getSelectedEntryIds();
} else {
entryIds = optEntryId;
}
var numEntries = countEntries(entryIds);
if (numEntries != 1) {
if (buttonName == "replyEmail" || buttonName == "replyText" || buttonName == "replyVM" || buttonName == "spam" || buttonName == "ditch" || buttonName == "forward" || buttonName == "editContact" || buttonName == "editTrans" || buttonName == "transApprove") {
alert("Please select one message and try again.");
return false;
}
}
if (numEntries == 0) {
if (buttonName == "delete" || buttonName == "replyEmail" || buttonName == "replyText" || buttonName == "replyVM" || buttonName == "spam" || buttonName == "moveSave" || buttonName == "moveTrash" || buttonName == "moveSpam" || buttonName == "ditch" || buttonName == "skip" || buttonName == "forward" || buttonName == "share" || buttonName == "unshare" || buttonName == "flag" || buttonName == "unflag" || buttonName == "editContact" || buttonName == "editTrans" || buttonName == "transApprove" || buttonName == "transcribe") {
alert("Please select a message by clicking the checkbox of the message and try again.");
return false;
}
}
if (buttonName == "replyEmail") {
openReplyDialog(entryIds, "email");
return false;
}
if (buttonName == "replyText") {
openReplyDialog(entryIds, "sms");
return false;
}
if (buttonName == "replyVM") {
openReplyDialog(entryIds, "vm");
return false;
}
if (buttonName == "spam") {
openSpamDialog(entryIds);
return false;
}
if (buttonName == "ditch") {
openDitchDialog(entryIds);
return false;
}
if (buttonName == "forward") {
openForwardDialog(entryIds);
return false;
}
if (buttonName == "editContact") {
openEditContactDialog(entryIds);
return false;
}
if (buttonName == "addUnknownContacts") {
addUnknownContacts();
return false;
}
if (buttonName == "editTrans") {
openTranscriptionDialog(entryIds);
return false;
}
if (buttonName == "transApprove") {
openTransApproveDialog(entryIds);
return false;
}
if (buttonName == "transcribe") {
toggleDisplay('pvMsgTranscribeRepl'); toggleDisplay('pvMsgOrigId');
}
var actionUrl = "/youmail/vm/inbox/buttonAction.do?buttonName=" + buttonName + "&entryIds=" + entryIds + "&pt=" + Youmail.vm.pageType;
inAjax.simpleRequest(actionUrl, buttonName);
}
function onMenuItemClick(p_sType, p_aArgs, p_oItem)
{
processAction(p_oItem.value);
}
function onDefaultMenuButtonPress(theEvent) {
var buttonId = theEvent.target.parentNode.parentNode.id;
var buttonKey = null;
if (buttonId == "replyButton") {
buttonKey = "replyEmail";
}
processAction(buttonKey);
}
function addUnknownContacts()
{
window.location="/youmail/contact/addBulkContacts.do?f=Inbox";
}
